import{_ as s,c as t,a2 as i,o as e}from"./chunks/framework.BQmytedh.js";const k=JSON.parse('{"title":"快速开始","description":"","frontmatter":{},"headers":[],"relativePath":"guide/getting-start.md","filePath":"guide/getting-start.md","lastUpdated":1732684903000}'),l={name:"guide/getting-start.md"};function n(h,a,o,r,p,d){return e(),t("div",null,a[0]||(a[0]=[i('<h1 id="快速开始" tabindex="-1">快速开始 <a class="header-anchor" href="#快速开始" aria-label="Permalink to &quot;快速开始&quot;">​</a></h1><h2 id="安装-volta" tabindex="-1">安装 Volta <a class="header-anchor" href="#安装-volta" aria-label="Permalink to &quot;安装 Volta&quot;">​</a></h2><h3 id="unix" tabindex="-1">Unix 安装 <a class="header-anchor" href="#unix" aria-label="Permalink to &quot;Unix 安装{#unix}&quot;">​</a></h3><p>在大多数 Unix 系统(包括 macOS)上，您可以使用一个命令安装 Volta:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">curl</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> https://get.volta.sh</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> |</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;"> bash</span></span></code></pre></div><p>对于 <a href="https://www.gnu.org/software/bash/" target="_blank" rel="noreferrer">bash</a>, <a href="https://www.zsh.org/" target="_blank" rel="noreferrer">zsh</a> 和 <a href="http://fishshell.com/" target="_blank" rel="noreferrer">fish</a>，这个安装程序将自动更新控制台启动脚本。如果您希望防止修改控制台启动脚本，请参阅跳过 Volta 设置。要手动配置你的 shell 使用 Volta，编辑你的控制台启动脚本如下:</p><ul><li>将 VOLTA_HOME 变量设置为$HOME/.volta <br></li><li>将$VOLTA_HOME/bin 添加到 PATH 变量的开头</li></ul><h3 id="windows-安装" tabindex="-1">Windows 安装 <a class="header-anchor" href="#windows-安装" aria-label="Permalink to &quot;Windows 安装&quot;">​</a></h3><p>对于 Windows，推荐使用 <a href="https://github.com/microsoft/winget-cli" target="_blank" rel="noreferrer">winget</a> 安装 Volta</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">winget</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> Volta.Volta</span></span></code></pre></div><p>如果您愿意，您可以直接<a href="https://github.com/volta-cli/volta/releases/latest" target="_blank" rel="noreferrer">下载</a>安装程序并手动运行它来安装 Volta。</p><h3 id="windows-下的-linux-子系统" tabindex="-1">Windows 下的 linux 子系统 <a class="header-anchor" href="#windows-下的-linux-子系统" aria-label="Permalink to &quot;Windows 下的 linux 子系统&quot;">​</a></h3><p>如果您在 Linux 的 Windows 子系统中使用 Volta，请遵循上面的 Unix 安装指南。</p><h2 id="选择默认的-node-版本" tabindex="-1">选择默认的 Node 版本 <a class="header-anchor" href="#选择默认的-node-版本" aria-label="Permalink to &quot;选择默认的 Node 版本&quot;">​</a></h2><p>这是 Volta 将在有固定版本的项目之外的其它地方使用的版本。</p><p>要选择特定版本的 Node，可以运行：</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">volta</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> node@20.11.0</span></span></code></pre></div><p>或者使用最新的 LTS 版本，可以运行：</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">volta</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> node</span></span></code></pre></div>',19)]))}const g=s(l,[["render",n]]);export{k as __pageData,g as default};
